mapper.select:
SELECT "m_calendar_id", "member_num", "date", "memo"
FROM membercalendar
WHERE "m_calendar_id" = #{ m_calendar_id} AND "member_num" = #{ member_num}

mapper.update:
UPDATE 
	(SELECT "member_num", "m_calendar_id", "memo"
	 FROM membercalendar
	 WHERE "member_num" = #{member_num})
SET "memo" = #{memo}
WHERE "m_calendar_id" = #{m_calendar_id}
