mapper.update:
UPDATE trainer_info
SET trainer_insta=#{trainer_insta}, trainer_intro=#{trainer_intro}
WHERE trainer_num=#{trainer_num}

UPDATE trainer_info
SET gym_name=#{gym_name}, gym_link=#{gym_link}
WHERE trainer_num=#{trainer_num}

mapper.select:
SELECT trainer_num, trainer_insta, trainer_intro, gym_name, gym_link
FROM trainer_info
WHERE trainer_num=#{trainer_num}
